package com.example.myapplication;import android.app.Activity;public class PlaneShooterGameManager extends GameManager {    private static PlaneShooterGameManager planeShooterGameManager;    Activity activity;    private PlaneShooterGameManager(Activity activity) {        this.activity = activity;    }    static PlaneShooterGameManager getInstance(Activity activity) {        if (planeShooterGameManager == null) {            planeShooterGameManager = new PlaneShooterGameManager(activity);        }        return planeShooterGameManager;    }    public void saveGame(GameStatus gameStatus) {        DBHandler.getInstance(activity).saveGameStatus(gameStatus, DBHandler.Game.SPACESHOOTER);    }    public ShooterGameStatus getGameStatus(String username) {        ShooterGameStatus hangmanGameStat =                (ShooterGameStatus)                        DBHandler.getInstance(activity)                                .getGameStatus(username, DBHandler.Game.SPACESHOOTER);        if (hangmanGameStat == null) {            hangmanGameStat = new ShooterGameStatus(username);        }        return hangmanGameStat;    }}