package com.example.myapplication.SpaceShooter;import android.app.Activity;import com.example.myapplication.DBHandler;import com.example.myapplication.GameManager;import com.example.myapplication.GameStatus;public class ShooterGameManager extends GameManager {    private static ShooterGameManager shooterGameManager;    Activity activity;    private ShooterGameManager(Activity activity) {        this.activity = activity;    }    public static ShooterGameManager getInstance(Activity activity) {        if (shooterGameManager == null) {            shooterGameManager = new ShooterGameManager(activity);        }        return shooterGameManager;    }    public void saveGame(GameStatus gameStatus) {        DBHandler.getInstance(activity).saveGameStatus(gameStatus, DBHandler.Game.SPACESHOOTER);    }    public ShooterGameStatus getGameStatus(String username) {        ShooterGameStatus shooterGameStatus =                (ShooterGameStatus)                        DBHandler.getInstance(activity)                                .getGameStatus(username, DBHandler.Game.SPACESHOOTER);        if (shooterGameStatus == null) {            shooterGameStatus = new ShooterGameStatus(username);        }        return shooterGameStatus;    }}