package com.example.myapplication.spaceshooter;import android.app.Activity;import com.example.myapplication.databaseconnector.GameEnum;import com.example.myapplication.domain.GameManager;import com.example.myapplication.databaseconnector.GameStatusDaoImpl;import com.example.myapplication.spaceshooter.shootergamestatus.ShooterCrossLevelManager;import com.example.myapplication.spaceshooter.shootergamestatus.ShooterGameLevelManager;import com.example.myapplication.spaceshooter.shootergamestatus.ShooterGameStatusFacade;/** The game manager for this Space Shooter game. */public class ShooterGameManager extends GameManager {  /**   * The singleton ShooterGameManager.   */  private static ShooterGameManager shooterGameManager;  /**   * Constructs a ShooterGameManager.   *   * @param activity the activity that called this ShooterGameManager.   */  private ShooterGameManager(Activity activity) {    super(activity);  }  /**   * Getter for this ShooterGameManager instance.   *   * @return ShooterGameManager the instance of this ShooterGameManager.   */  public static ShooterGameManager getInstance(Activity activity) {    if (shooterGameManager == null) {      shooterGameManager = new ShooterGameManager(activity);    }    return shooterGameManager;  }  /**   * Returns the ShooterGameStatusFacade instance that belongs to a user with specified username.   *   * @param username the username of the user playing this game.   * @return ShooterGameStatusFacade the ShooterGameStatusFacade instance of this user.   */  public ShooterGameStatusFacade getGameStatus(String username) {    ShooterGameStatusFacade shooterGameStatus =            (ShooterGameStatusFacade)                    GameStatusDaoImpl.getInstance(getActivity()).getGameStatus(username, GameEnum.SPACESHOOTER);    if (shooterGameStatus == null) {      shooterGameStatus = new ShooterGameStatusFacade(username);      shooterGameStatus.setShooterGameLevelManager(new ShooterGameLevelManager());      shooterGameStatus.setShooterCrossLevelManager(new ShooterCrossLevelManager());    }    return shooterGameStatus;  }}