package com.example.myapplication.spaceshooter;import android.app.Activity;import com.example.myapplication.databaseconnector.GameEnum;import com.example.myapplication.domain.GameManager;import com.example.myapplication.domain.GameStatus;import com.example.myapplication.databaseconnector.GameStatusDaoImpl;public class ShooterGameManager extends GameManager {  private static ShooterGameManager shooterGameManager;  private Activity activity;  private ShooterGameManager(Activity activity) {    this.activity = activity;  }  public static ShooterGameManager getInstance(Activity activity) {    if (shooterGameManager == null) {      shooterGameManager = new ShooterGameManager(activity);    }    return shooterGameManager;  }  public void saveGame(GameStatus gameStatus) {    GameStatusDaoImpl.getInstance(activity).saveGameStatus(gameStatus, GameEnum.SPACESHOOTER);  }  public ShooterGameStatus getGameStatus(String username) {    ShooterGameStatus shooterGameStatus =        (ShooterGameStatus)            GameStatusDaoImpl.getInstance(activity).getGameStatus(username, GameEnum.SPACESHOOTER);    if (shooterGameStatus == null) {      shooterGameStatus = new ShooterGameStatus(username);    }    return shooterGameStatus;  }}