package com.example.myapplication.spaceshooter;import android.app.Activity;import com.example.myapplication.databaseconnector.GameEnum;import com.example.myapplication.domain.GameManager;import com.example.myapplication.domain.GameStatus;import com.example.myapplication.databaseconnector.GameStatusDaoImpl;/** The game manager for this Space Shooter game. */public class ShooterGameManager extends GameManager {  /**   * The singleton ShooterGameManager.   */  private static ShooterGameManager shooterGameManager;  /**   * Constructs a ShooterGameManager.   *   * @param activity the activity that called this ShooterGameManager.   */  private ShooterGameManager(Activity activity) {    super(activity);  }  /**   * Getter for this ShooterGameManager instance.   *   * @return ShooterGameManager the instance of this ShooterGameManager.   */  public static ShooterGameManager getInstance(Activity activity) {    if (shooterGameManager == null) {      shooterGameManager = new ShooterGameManager(activity);    }    return shooterGameManager;  }  /**   * Returns the ShooterGameStatus instance that belongs to a user with specified username.   *   * @param username the username of the user playing this game.   * @return ShooterGameStatus the ShooterGameStatus instance of this user.   */  public ShooterGameStatus getGameStatus(String username) {    ShooterGameStatus shooterGameStatus =            (ShooterGameStatus)                    GameStatusDaoImpl.getInstance(getActivity()).getGameStatus(username, GameEnum.SPACESHOOTER);    if (shooterGameStatus == null) {      shooterGameStatus = new ShooterGameStatus(username);    }    return shooterGameStatus;  }}